// Complete fixed version with all variables defined
const message = $input.first()?.json?.message?.text?.toLowerCase() || "";

// 1. First define the base country map
const countryMap = {
  // Standard countries
  'india': 'in', 'indian': 'in', 'in': 'in', 'bharat': 'in',
  'japan': 'jp', 'japanese': 'jp', 'jp': 'jp', 'nihon': 'jp',
  'us': 'us', 'usa': 'us', 'america': 'us', 'united states': 'us',
  'uk': 'gb', 'britain': 'gb', 'england': 'gb', 'united kingdom': 'gb',
  'canada': 'ca', 'canadian': 'ca', 'ca': 'ca',
  'australia': 'au', 'aus': 'au', 'oz': 'au',
  'germany': 'de', 'deutschland': 'de', 'german': 'de',
  'france': 'fr', 'french': 'fr', 'paris': 'fr',
  'china': 'cn', 'chinese': 'cn', 'beijing': 'cn',

  // Middle East additions
  'iran': 'ir', 'persian': 'ir', 'tehran': 'ir',
  'israel': 'il', 'jerusalem': 'il', 'tel aviv': 'il',
  'palestine': 'ps', 'gaza': 'ps', 'west bank': 'ps'
};

// 2. Special conflict cases
const conflictKeywords = {
  'iran israel': { topic: 'war', countries: ['ir', 'il'] },
  'israel iran': { topic: 'war', countries: ['il', 'ir'] },
  'middle east': { topic: 'war', countries: ['il', 'ir', 'sy', 'iq'] },
  'gaza': { topic: 'war', countries: ['ps', 'il'] }
};

// 3. Topic mapping
const topicMap = {
  'tech': 'technology', 'technology': 'technology', 'computer': 'technology',
  'business': 'business', 'finance': 'business', 'economy': 'business',
  'sports': 'sports', 'sport': 'sports', 'football': 'sports', 'cricket': 'sports',
  'entertainment': 'entertainment', 'movie': 'entertainment', 'bollywood': 'entertainment',
  'health': 'health', 'medical': 'health', 'hospital': 'health',
  'science': 'science', 'space': 'science', 'physics': 'science',
  'war': 'war', 'conflict': 'war', 'attack': 'war', 'military': 'war'
};

// --- Processing Logic --- //

// Check for conflicts first
let detectedConflict = null;
for (const [term, data] of Object.entries(conflictKeywords)) {
  if (message.includes(term)) {
    detectedConflict = data;
    break;
  }
}

if (detectedConflict) {
  return [{
    json: {
      topic: detectedConflict.topic,
      country: detectedConflict.countries.join(','),
      isConflict: true,
      originalMessage: message
    }
  }];
}

// Normal detection
let country = 'us';
let topic = 'general';

// Country detection
const words = message.split(/\s+/);
for (const word of words) {
  if (countryMap[word]) {
    country = countryMap[word];
    break;
  }
}

// Topic detection
for (const [keyword, category] of Object.entries(topicMap)) {
  if (message.includes(keyword)) {
    topic = category;
    break;
  }
}

return [{
  json: {
    topic,
    country,
    isConflict: false,
    originalMessage: message
  }
}];

const articles = $input.first().json.articles;
const metadata = $node["Detect Topic & Country"].json;

// Get country name for display
const countryNames = {
  'in': 'India', 'jp': 'Japan', 'us': 'USA', 
  'gb': 'UK', 'ca': 'Canada', 'au': 'Australia',
  'de': 'Germany', 'fr': 'France', 'cn': 'China'
};

if (!articles || articles.length === 0) {
  return [{ json: { 
    response: `âŒ No ${metadata.topic} news found for ${countryNames[metadata.country] || metadata.country}.\nTry: "news in india" or "tech japan"` 
  }}];
}

let response = `ðŸŒŽ *${metadata.topic.toUpperCase()} NEWS - ${countryNames[metadata.country] || metadata.country.toUpperCase()}*\n\n`;

articles.forEach((article, index) => {
  const date = new Date(article.publishedAt).toLocaleDateString();
  response += `ðŸ“Œ *${article.title}*\n`;
  response += `ðŸ“¡ ${article.source.name} | ðŸ“… ${date}\n`;
  response += `${article.description || ''}\n`;
  response += `ðŸ”— [Read More](${article.url})\n\n`;
});

return [{ json: { response } }];
